import React from 'react';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

var button = document.getElementsByTagName("button");

const socket_buy = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});

const socket_sell = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});


socket_buy.on('BUY', (data) => {
  console.log(data.volume);
}); 



function cerrar_conexion() {
  if (socket_buy.connected){
    console.log('Desactivando conexion');
    socket_buy.close();
  }
  else{
    console.log('Activando conexion');
    socket_buy.connect();
  }
};





function App() {
  return (
   <div>
     <h1>Hola Programa</h1>
     <button onClick = {cerrar_conexion}> Server Conectado </button>

   </div>
  )};

export default App;
