import React from 'react';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

const socket = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});


socket.on('BUY', (data) => {
  console.log(data.volume);
}); 



function cerrar_conexion(socket) {
  socket.close();
};






function App() {
  return (
   <div>
     <h1>Hola Programa</h1>
     <button onClick = {cerrar_conexion(socket)} >Interrumpir conexion </ button>

   </div>
  )};

export default App;
