import React from 'react';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';

var button = document.getElementsByTagName("button");

const socket = io('wss://le-18262636.bitzonte.com', {
  path: '/stocks'
});


socket.on('BUY', (data) => {
  console.log(data.volume);
}); 



function cerrar_conexion() {
  if (socket.connected){
    socket.close();
  }
  else{
    socket.connect();
  }
};

function toggleText(button_id)
{                      //-----\/ 'button_id' - > button_id
   if (document.getElementbyId(button_id).innerText == "Server Conectado") 
   {
       document.getElementbyId(button_id).innerText = "Server Desconectado";
   }
   else 
   {
     document.getElementById(button_id).innerText = "Server Conectado";
   }
}





function App() {
  return (
   <div>
     <h1>Hola Programa</h1>
     <button id={'offline'} onClick = {toggleText(this.id)}  >Server Conectado</button>

   </div>
  )};

export default App;
